import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';



const API_URL = 'http://localhost:8080/v0.1';
const ORGANIZATION_ID = 1;
const PERSON_ID = 1;
const localization = {
	locale: 'en-US',
	currency: 'USD'
}
const accountTypes = [
    {id: 1, name: "Assets"},
    {id: 2, name: "Liabilities"},
    {id: 3, name: "Owner's Equity"},
    {id: 4, name: "Income"},
    {id: 5, name: "Expenses"},
]

class Accounts extends React.Component {
	constructor(props) {
		super(props);
		this.state = { 
            accounts: [],
            categories: []
        };
	}

    componentDidMount() {
        const url = `${API_URL}/organization/${ORGANIZATION_ID}/accountBalance`;
        axios.get(url).then(response => {
            this.setState({accounts: response.data});
        });
        axios.get(`${API_URL}/organization/${ORGANIZATION_ID}/categoryBalance`).then(response => {
            this.setState({categories: response.data});
        })
    }

	render() {


		return (
			<div>
				<ol className="breadcrumb float-xl-right">
					<li className="breadcrumb-item"><Link to="/">Home</Link></li>
					<li className="breadcrumb-item active">Accounts</li>
				</ol>
				<h1 className="page-header">Accounts </h1>
				<div>
                    {accountTypes.map((accountType, i) => {
                        return (
                            <div key={accountType.id}>
                                <h3>{accountType.name}</h3>
                                <div>
                                    {this.state.accounts.filter(account => account.accountTypeId == accountType.id).map(account => {
                                        return(
                                            <div key={account.accountId} className="px-5"><Link to='#'><span>{account.accountName}</span><span>{account.debitTotal}</span><span>{account.creditTotal}</span></Link></div>
                                        )
                                    })}
                                </div>
                            </div>
                        )
                    })}
                </div>
			</div>
		)
	}
}

export default Accounts;
